mapper.select:
SELECT p.*, a.STORED_FILE_NAME
FROM t_product AS p 



		AND p.CATE_CODE in (
		(SELECT CATE_CODE
		FROM t_category
		WHERE UPPER_CATE_CODE = #{cateCode}
		)
		)


		AND p.CATE_CODE = #{cateCode}


ORDER BY p.UPD_DATE DESC, p.PROD_ID DESC
LIMIT #{pageStart}, #{perPageNum}

SELECT COUNT(*)
FROM t_product


		AND CATE_CODE in (
		(SELECT CATE_CODE
		FROM t_category
		WHERE UPPER_CATE_CODE = #{cateCode}
		)
		)


		AND CATE_CODE = #{cateCode}
