mapper.select:
SELECT p.PROD_ID, p.STATUS_CODE, c.CMM_CODE_NAME AS STATUS_NAME, p.TITLE, p.PRICE, p.UPD_DATE, a.STORED_FILE_NAME
FROM t_product AS p INNER JOIN t_cmm_code AS c
ON p.STATUS_CODE = c.CMM_CODE

WHERE p.USER_ID = #{userId}

ORDER BY p.UPD_DATE DESC, p.PROD_ID DESC
LIMIT #{pageStart}, #{perPageNum}

SELECT STORED_FILE_NAME, ORIG_FILE_NAME, FILE_SIZE
FROM t_attach


		AND PROD_ID = #{prodId}

mapper.update:
UPDATE t_product

SET CATE_CODE = #{cateCode}
,	TRADE_CODE = #{tradeCode}
,	STATUS_CODE = #{statusCode}
,	TITLE = #{title}
,	PRICE = #{price}
,	DES = #{des}
,	STOCK = #{stock}
,	UPD_DATE = now()

WHERE PROD_ID = #{prodId}

mapper.insert:
INSERT INTO t_attach(
	STORED_FILE_NAME
,	ORIG_FILE_NAME
,	PROD_ID
,	FILE_SIZE

) VALUES (
	#{storedFileName}
,	#{origFileName}
,	#{prodId}
,	#{fileSize}
)
